import Sticky from '@/components/Sticky' // 粘性header组件
import { createUser } from '@/api/user'
import { getAllRole } from '@/api/role'

const defaultForm = {
  name: '', // 姓名
  nickname: '', // 昵称
  password: '', // 密码
  phone: '', // 手机号
  id: undefined,
  status: true,
  checkAll: false,
  checkedRoles: [],
  isIndeterminate: true
}
export default {
  name: 'UserDetail',
  components: { Sticky },
  props: {
    isEdit: {
      type: Boolean,
      default: false
    }
  },
  data() {
    const validateRequire = (rule, value, callback) => {
      if (value === '') {
        this.$message({
          message: rule.field + '为必填项',
          type: 'error'
        })
        callback(new Error(rule.field + '为必填项'))
      } else {
        callback()
      }
    }
    const validatePass2 = (rule, value, callback) => {
      if (value === '') {
        callback(new Error('请再次输入密码'))
      } else if (value !== this.postForm.password) {
        callback(new Error('两次输入密码不一致!'))
      } else {
        callback()
      }
    }
    return {
      postForm: Object.assign({}, defaultForm),
      loading: false,
      userListOptions: [],
      roleOptions: [],

      rules: {
        name: [{ validator: validateRequire }],
        nickname: [{ validator: validateRequire }],
        password: [{ validator: validateRequire }],
        repassword: [{ validator: validatePass2 }]
      },
      tempRoute: {}
    }
  },
  computed: {
    lang() {
      return this.$store.getters.language
    }
  },
  created() {
    this.fetchRoleData()
    // Why need to make a copy of this.$route here?
    // Because if you enter this page and quickly switch tag, may be in the execution of the setTagsViewTitle function, this.$route is no longer pointing to the current page
    // https://github.com/PanJiaChen/vue-element-admin/issues/1221
    this.tempRoute = Object.assign({}, this.$route)
  },
  methods: {
    fetchRoleData() {
      getAllRole().then(response => {
        this.roleOptions = response.data
        // console.log(this.roleOptions)
      }).catch(err => {
        console.log(err)
      })
    },
    handleCheckAllChange(val) {
      this.postForm.checkedRoles = val ? this.roleOptions : []
      this.postForm.isIndeterminate = false
    },
    handlecheckedRolesChange(value) {
      this.checkedCount = value.length
      this.postForm.checkAll = this.checkedCount === this.roleOptions.length
      this.postForm.isIndeterminate = this.checkedCount > 0 && this.checkedCount < this.roleOptions.length
      //
    },
    submitForm() {
      this.loading = true
      // console.log(this.postForm)
      this.$refs.postForm.validate(valid => {
        if (!valid) {
          this.loading = false
          return false
        }
        createUser(this.postForm).then(() => {
          this.loading = false
          this.$notify({
            title: 'Success',
            dangerouslyUseHTMLString: true,
            message: '添加成功',
            type: 'success',
            duration: 2000
          })
        })
      })
    }
  }
}
